#!/usr/bin/env python3\n\"\"\"\nìŒì„± íŒŒì¼ ê¸°ë°˜ í‚¤ì˜¤ìŠ¤í¬ ì‹œìŠ¤í…œ ì‹œê°í™” ì°¨íŠ¸ ìƒì„± ìŠ¤í¬ë¦½íŠ¸\në‹¨ìˆœí™”ëœ ì‹œìŠ¤í…œì— ë§ê²Œ ì—…ë°ì´íŠ¸ë¨\n\"\"\"\n\nimport matplotlib\nmatplotlib.use('Agg')  # GUI ì—†ì´ ì‹¤í–‰\nimport matplotlib.pyplot as plt\nimport matplotlib.patches as mpatches\nimport numpy as np\nfrom pathlib import Path\n\ntry:\n    import seaborn as sns\nexcept ImportError:\n    print(\"âš ï¸ seabornì´ ì„¤ì¹˜ë˜ì§€ ì•Šì•˜ìŠµë‹ˆë‹¤. ê¸°ë³¸ matplotlibë§Œ ì‚¬ìš©í•©ë‹ˆë‹¤.\")\n\n# í•œê¸€ í°íŠ¸ ì„¤ì •\ntry:\n    plt.rcParams['font.family'] = ['Malgun Gothic', 'DejaVu Sans']\nexcept:\n    plt.rcParams['font.family'] = ['DejaVu Sans']\nplt.rcParams['axes.unicode_minus'] = False\n\n# ì¶œë ¥ ë””ë ‰í† ë¦¬ ìƒì„±\noutput_dir = Path('presentation/charts')\noutput_dir.mkdir(exist_ok=True)\nprint(f\"ğŸ“ ì¶œë ¥ ë””ë ‰í† ë¦¬: {output_dir.absolute()}\")\n\nprint(\"ğŸ¨ ì‹œê°í™” ì°¨íŠ¸ ìƒì„± ì¤‘...\")\n\n# 1. ì•„í‚¤í…ì²˜ ë‹¤ì´ì–´ê·¸ë¨\ndef create_architecture_diagram():\n    fig, ax = plt.subplots(figsize=(14, 10))\n    ax.set_xlim(0, 12)\n    ax.set_ylim(0, 10)\n    ax.axis('off')\n    \n    # ì œëª©\n    ax.text(6, 9.5, 'ğŸ¤ ìŒì„± íŒŒì¼ ê¸°ë°˜ í‚¤ì˜¤ìŠ¤í¬ ì‹œìŠ¤í…œ ì•„í‚¤í…ì²˜', \n            fontsize=18, fontweight='bold', ha='center')\n    \n    # ì²˜ë¦¬ ë‹¨ê³„ ì •ì˜ (ë‹¨ìˆœí™”ë¨)\n    steps = [\n        {\"name\": \"ğŸ“ ìŒì„± íŒŒì¼ ë¡œë“œ\", \"x\": 2, \"y\": 8, \"color\": \"#E3F2FD\"},\n        {\"name\": \"ğŸ”Š ìŒì„± ì „ì²˜ë¦¬\", \"x\": 2, \"y\": 7, \"color\": \"#F3E5F5\"},\n        {\"name\": \"ğŸ—£ï¸ ìŒì„±ì¸ì‹ (Whisper)\", \"x\": 2, \"y\": 6, \"color\": \"#E8F5E8\"},\n        {\"name\": \"ğŸ§  ì˜ë„ íŒŒì•… (GPT-4o)\", \"x\": 2, \"y\": 5, \"color\": \"#FFF3E0\"},\n        {\"name\": \"ğŸ” ë©”ë‰´ ê´€ë¦¬\", \"x\": 2, \"y\": 4, \"color\": \"#FFEBEE\"},\n        {\"name\": \"ğŸ“‹ ì£¼ë¬¸ ì²˜ë¦¬\", \"x\": 2, \"y\": 3, \"color\": \"#F1F8E9\"},\n        {\"name\": \"ğŸ“ ì‘ë‹µ ìƒì„±\", \"x\": 2, \"y\": 2, \"color\": \"#E0F2F1\"},\n        {\"name\": \"ğŸ”Š TTS ì¶œë ¥\", \"x\": 2, \"y\": 1, \"color\": \"#FFF8E1\"}\n    ]\n    \n    # ì²˜ë¦¬ ì‹œê°„ ì •ë³´\n    processing_times = [\"ì¦‰ì‹œ\", \"1ì´ˆ\", \"2-5ì´ˆ\", \"1-3ì´ˆ\", \"0.5ì´ˆ\", \"0.5ì´ˆ\", \"1-2ì´ˆ\", \"2-4ì´ˆ\"]\n    \n    # ê° ë‹¨ê³„ ê·¸ë¦¬ê¸°\n    for i, step in enumerate(steps):\n        # í”„ë¡œì„¸ìŠ¤ ë°•ìŠ¤\n        rect = mpatches.Rectangle((step[\"x\"]-1, step[\"y\"]-0.3), 4, 0.6,\n                                 facecolor=step[\"color\"], edgecolor='black', linewidth=1)\n        ax.add_patch(rect)\n        \n        # í”„ë¡œì„¸ìŠ¤ ì´ë¦„\n        ax.text(step[\"x\"]+1, step[\"y\"], step[\"name\"], fontsize=12, fontweight='bold',\n                ha='center', va='center')\n        \n        # ì²˜ë¦¬ ì‹œê°„\n        ax.text(step[\"x\"]+5, step[\"y\"], f\"â±ï¸ {processing_times[i]}\", fontsize=10,\n                ha='left', va='center', style='italic')\n        \n        # í™”ì‚´í‘œ (ë§ˆì§€ë§‰ ë‹¨ê³„ ì œì™¸)\n        if i < len(steps) - 1:\n            ax.annotate('', xy=(step[\"x\"]+1, step[\"y\"]-0.4), \n                       xytext=(step[\"x\"]+1, step[\"y\"]-0.6),\n                       arrowprops=dict(arrowstyle='->', lw=2, color='#666666'))\n    \n    # ì…ë ¥ ë°©ì‹ í‘œì‹œ\n    input_box = mpatches.Rectangle((8, 7.5), 3, 1.5, \n                                  facecolor='#E1F5FE', edgecolor='blue', linewidth=2)\n    ax.add_patch(input_box)\n    ax.text(9.5, 8.5, 'ğŸ“ ì…ë ¥ ë°©ì‹', fontsize=12, fontweight='bold', ha='center')\n    ax.text(9.5, 8.1, 'â€¢ ë¡œì»¬ íŒŒì¼', fontsize=10, ha='center')\n    ax.text(9.5, 7.8, 'â€¢ ì„œë²„ ì—…ë¡œë“œ', fontsize=10, ha='center')\n    \n    # ì¶œë ¥ ë°©ì‹ í‘œì‹œ\n    output_box = mpatches.Rectangle((8, 0.5), 3, 1.5, \n                                   facecolor='#F3E5F5', edgecolor='purple', linewidth=2)\n    ax.add_patch(output_box)\n    ax.text(9.5, 1.5, 'ğŸ”Š ì¶œë ¥ ë°©ì‹', fontsize=12, fontweight='bold', ha='center')\n    ax.text(9.5, 1.1, 'â€¢ í…ìŠ¤íŠ¸ ì‘ë‹µ', fontsize=10, ha='center')\n    ax.text(9.5, 0.8, 'â€¢ TTS ìŒì„±', fontsize=10, ha='center')\n    \n    plt.tight_layout()\n    plt.savefig(output_dir / 'architecture_diagram.png', dpi=300, bbox_inches='tight')\n    plt.close()\n    print(\"âœ… ì•„í‚¤í…ì²˜ ë‹¤ì´ì–´ê·¸ë¨ ìƒì„± ì™„ë£Œ\")\n\n# 2. ì„±ëŠ¥ ë¶„ì„ ì°¨íŠ¸\ndef create_performance_analysis():\n    fig, ((ax1, ax2), (ax3, ax4)) = plt.subplots(2, 2, figsize=(15, 12))\n    fig.suptitle('ğŸ“Š ì‹œìŠ¤í…œ ì„±ëŠ¥ ë¶„ì„', fontsize=16, fontweight='bold')\n    \n    # ì²˜ë¦¬ ì‹œê°„ ë¶„ì„\n    stages = ['íŒŒì¼ ë¡œë“œ', 'ì „ì²˜ë¦¬', 'STT', 'ì˜ë„íŒŒì•…', 'ì£¼ë¬¸ì²˜ë¦¬', 'ì‘ë‹µìƒì„±', 'TTS']\n    times = [0.1, 1.0, 3.5, 2.0, 0.5, 1.5, 3.0]  # ì´ˆ ë‹¨ìœ„\n    colors = ['#FF9999', '#66B2FF', '#99FF99', '#FFCC99', '#FF99CC', '#99CCFF', '#FFB366']\n    \n    bars = ax1.bar(stages, times, color=colors)\n    ax1.set_title('â±ï¸ ë‹¨ê³„ë³„ ì²˜ë¦¬ ì‹œê°„', fontweight='bold')\n    ax1.set_ylabel('ì²˜ë¦¬ ì‹œê°„ (ì´ˆ)')\n    ax1.tick_params(axis='x', rotation=45)\n    \n    # ë§‰ëŒ€ ìœ„ì— ê°’ í‘œì‹œ\n    for bar, time in zip(bars, times):\n        height = bar.get_height()\n        ax1.text(bar.get_x() + bar.get_width()/2., height + 0.1,\n                f'{time}ì´ˆ', ha='center', va='bottom')\n    \n    # íŒŒì¼ í¬ê¸°ë³„ ì²˜ë¦¬ ì‹œê°„\n    file_sizes = ['1MB', '3MB', '5MB', '8MB', '10MB']\n    processing_times = [8.2, 10.5, 13.1, 16.8, 20.2]\n    \n    ax2.plot(file_sizes, processing_times, marker='o', linewidth=2, markersize=8, color='#2E8B57')\n    ax2.set_title('ğŸ“ íŒŒì¼ í¬ê¸°ë³„ ì²˜ë¦¬ ì‹œê°„', fontweight='bold')\n    ax2.set_ylabel('ì´ ì²˜ë¦¬ ì‹œê°„ (ì´ˆ)')\n    ax2.grid(True, alpha=0.3)\n    \n    # ì •í™•ë„ ë¶„ì„\n    categories = ['ì¼ë°˜ ì£¼ë¬¸', 'ë³µí•© ì£¼ë¬¸', 'ë³€ê²½ ìš”ì²­', 'ì·¨ì†Œ ìš”ì²­', 'ë¬¸ì˜ì‚¬í•­']\n    accuracy = [95.2, 89.7, 92.1, 96.8, 87.3]\n    \n    bars = ax3.bar(categories, accuracy, color='#4CAF50')\n    ax3.set_title('ğŸ¯ ì¹´í…Œê³ ë¦¬ë³„ ì •í™•ë„', fontweight='bold')\n    ax3.set_ylabel('ì •í™•ë„ (%)')\n    ax3.set_ylim(80, 100)\n    ax3.tick_params(axis='x', rotation=45)\n    \n    # ë§‰ëŒ€ ìœ„ì— ê°’ í‘œì‹œ\n    for bar, acc in zip(bars, accuracy):\n        height = bar.get_height()\n        ax3.text(bar.get_x() + bar.get_width()/2., height + 0.5,\n                f'{acc}%', ha='center', va='bottom')\n    \n    # ì‹œìŠ¤í…œ ë¦¬ì†ŒìŠ¤ ì‚¬ìš©ëŸ‰\n    resources = ['CPU', 'Memory', 'Disk I/O', 'Network']\n    usage = [45, 62, 23, 18]  # í¼ì„¼íŠ¸\n    colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4']\n    \n    wedges, texts, autotexts = ax4.pie(usage, labels=resources, colors=colors, autopct='%1.1f%%')\n    ax4.set_title('ğŸ’» ì‹œìŠ¤í…œ ë¦¬ì†ŒìŠ¤ ì‚¬ìš©ëŸ‰', fontweight='bold')\n    \n    plt.tight_layout()\n    plt.savefig(output_dir / 'performance_analysis.png', dpi=300, bbox_inches='tight')\n    plt.close()\n    print(\"âœ… ì„±ëŠ¥ ë¶„ì„ ì°¨íŠ¸ ìƒì„± ì™„ë£Œ\")\n\n# 3. ê¸°ìˆ  ìŠ¤íƒ ì°¨íŠ¸\ndef create_technology_stack():\n    fig, ax = plt.subplots(figsize=(12, 8))\n    ax.set_xlim(0, 10)\n    ax.set_ylim(0, 8)\n    ax.axis('off')\n    \n    # ì œëª©\n    ax.text(5, 7.5, 'ğŸ”§ ê¸°ìˆ  ìŠ¤íƒ', fontsize=18, fontweight='bold', ha='center')\n    \n    # ê¸°ìˆ  ìŠ¤íƒ ë ˆì´ì–´\n    layers = [\n        {\"name\": \"ğŸ¤ ì‚¬ìš©ì ì¸í„°í˜ì´ìŠ¤\", \"techs\": [\"ğŸ“ íŒŒì¼ ì…ë ¥\", \"ğŸ“¡ ì„œë²„ ì—…ë¡œë“œ\"], \"y\": 6.5, \"color\": \"#E3F2FD\"},\n        {\"name\": \"ğŸŒ API ì„œë²„\", \"techs\": [\"FastAPI\", \"Uvicorn\", \"Pydantic\"], \"y\": 5.5, \"color\": \"#F3E5F5\"},\n        {\"name\": \"ğŸ§  AI/ML ëª¨ë¸\", \"techs\": [\"OpenAI Whisper\", \"GPT-4o\", \"OpenAI TTS\"], \"y\": 4.5, \"color\": \"#E8F5E8\"},\n        {\"name\": \"ğŸ”Š ìŒì„± ì²˜ë¦¬\", \"techs\": [\"librosa\", \"soundfile\", \"numpy\"], \"y\": 3.5, \"color\": \"#FFF3E0\"},\n        {\"name\": \"ğŸ’¾ ë°ì´í„° ì²˜ë¦¬\", \"techs\": [\"pathlib\", \"json\", \"logging\"], \"y\": 2.5, \"color\": \"#FFEBEE\"},\n        {\"name\": \"ğŸ ëŸ°íƒ€ì„\", \"techs\": [\"Python 3.x\", \"asyncio\", \"multiprocessing\"], \"y\": 1.5, \"color\": \"#F1F8E9\"}\n    ]\n    \n    for layer in layers:\n        # ë ˆì´ì–´ ë°•ìŠ¤\n        rect = mpatches.Rectangle((0.5, layer[\"y\"]-0.3), 9, 0.6,\n                                 facecolor=layer[\"color\"], edgecolor='black', linewidth=1)\n        ax.add_patch(rect)\n        \n        # ë ˆì´ì–´ ì´ë¦„\n        ax.text(1, layer[\"y\"], layer[\"name\"], fontsize=12, fontweight='bold', va='center')\n        \n        # ê¸°ìˆ ë“¤\n        tech_text = \" | \".join(layer[\"techs\"])\n        ax.text(3.5, layer[\"y\"], tech_text, fontsize=10, va='center')\n    \n    plt.tight_layout()\n    plt.savefig(output_dir / 'technology_stack.png', dpi=300, bbox_inches='tight')\n    plt.close()\n    print(\"âœ… ê¸°ìˆ  ìŠ¤íƒ ì°¨íŠ¸ ìƒì„± ì™„ë£Œ\")\n\n# 4. ë°ì´í„° íë¦„ ë‹¤ì´ì–´ê·¸ë¨\ndef create_data_flow_diagram():\n    fig, ax = plt.subplots(figsize=(14, 10))\n    ax.set_xlim(0, 14)\n    ax.set_ylim(0, 10)\n    ax.axis('off')\n    \n    # ì œëª©\n    ax.text(7, 9.5, 'ğŸ”„ ë°ì´í„° íë¦„ ë‹¤ì´ì–´ê·¸ë¨', fontsize=18, fontweight='bold', ha='center')\n    \n    # ë°ì´í„° íë¦„ ë‹¨ê³„\n    flow_steps = [\n        {\"name\": \"ğŸ‘¤ ì‚¬ìš©ì\", \"x\": 1, \"y\": 8, \"data\": \"ìŒì„± íŒŒì¼\"},\n        {\"name\": \"â±ï¸ íŒŒì¼ ë¡œë“œ\", \"x\": 3, \"y\": 8, \"data\": \"ë°”ì´ë„ˆë¦¬ ë°ì´í„°\"},\n        {\"name\": \"ğŸ¤ ì „ì²˜ë¦¬\", \"x\": 5, \"y\": 8, \"data\": \"ì •ê·œí™”ëœ ì˜¤ë””ì˜¤\"},\n        {\"name\": \"ğŸ”Š STT\", \"x\": 7, \"y\": 8, \"data\": \"í…ìŠ¤íŠ¸\"},\n        {\"name\": \"ğŸ—£ï¸ ì˜ë„íŒŒì•…\", \"x\": 9, \"y\": 8, \"data\": \"êµ¬ì¡°í™”ëœ ì˜ë„\"},\n        {\"name\": \"ğŸ§  ëŒ€í™”ì²˜ë¦¬\", \"x\": 11, \"y\": 8, \"data\": \"ì‘ë‹µ ê³„íš\"},\n        {\"name\": \"ğŸ“‹ ì£¼ë¬¸ê´€ë¦¬\", \"x\": 13, \"y\": 8, \"data\": \"ì£¼ë¬¸ ìƒíƒœ\"},\n        {\"name\": \"ğŸ“ ì‘ë‹µìƒì„±\", \"x\": 11, \"y\": 6, \"data\": \"ì‘ë‹µ í…ìŠ¤íŠ¸\"},\n        {\"name\": \"ğŸ”Š TTS\", \"x\": 9, \"y\": 6, \"data\": \"ìŒì„± íŒŒì¼\"},\n        {\"name\": \"ğŸ‘¤ ì‚¬ìš©ì\", \"x\": 7, \"y\": 6, \"data\": \"ìŒì„± ì‘ë‹µ\"}\n    ]\n    \n    # ê° ë‹¨ê³„ ê·¸ë¦¬ê¸°\n    for i, step in enumerate(flow_steps):\n        # í”„ë¡œì„¸ìŠ¤ ì›\n        circle = mpatches.Circle((step[\"x\"], step[\"y\"]), 0.4, \n                               facecolor='lightblue', edgecolor='navy', linewidth=2)\n        ax.add_patch(circle)\n        \n        # í”„ë¡œì„¸ìŠ¤ ì´ë¦„\n        ax.text(step[\"x\"], step[\"y\"]+0.7, step[\"name\"], fontsize=10, fontweight='bold',\n                ha='center', va='center')\n        \n        # ë°ì´í„° íƒ€ì…\n        ax.text(step[\"x\"], step[\"y\"]-0.7, step[\"data\"], fontsize=8,\n                ha='center', va='center', style='italic')\n        \n        # í™”ì‚´í‘œ (íŠ¹ì • ì—°ê²°ë§Œ)\n        if i < 6:  # ì²« ë²ˆì§¸ í–‰\n            if i < 5:\n                ax.annotate('', xy=(step[\"x\"]+0.5, step[\"y\"]), \n                           xytext=(step[\"x\"]+1.5, step[\"y\"]),\n                           arrowprops=dict(arrowstyle='->', lw=2, color='green'))\n        elif i == 6:  # ì£¼ë¬¸ê´€ë¦¬ì—ì„œ ì‘ë‹µìƒì„±ìœ¼ë¡œ\n            ax.annotate('', xy=(11.4, 6.4), xytext=(12.6, 7.6),\n                       arrowprops=dict(arrowstyle='->', lw=2, color='green'))\n        elif i >= 7 and i < 9:  # ë‘ ë²ˆì§¸ í–‰\n            ax.annotate('', xy=(step[\"x\"]-0.5, step[\"y\"]), \n                       xytext=(step[\"x\"]-1.5, step[\"y\"]),\n                       arrowprops=dict(arrowstyle='->', lw=2, color='green'))\n    \n    plt.tight_layout()\n    plt.savefig(output_dir / 'data_flow_diagram.png', dpi=300, bbox_inches='tight')\n    plt.close()\n    print(\"âœ… ë°ì´í„° íë¦„ ë‹¤ì´ì–´ê·¸ë¨ ìƒì„± ì™„ë£Œ\")\n\n# 5. ëª¨ë“ˆ êµ¬ì¡° ì°¨íŠ¸\ndef create_module_structure():\n    fig, ax = plt.subplots(figsize=(12, 10))\n    ax.set_xlim(0, 12)\n    ax.set_ylim(0, 10)\n    ax.axis('off')\n    \n    # ì œëª©\n    ax.text(6, 9.5, 'ğŸ“¦ ëª¨ë“ˆ êµ¬ì¡°', fontsize=18, fontweight='bold', ha='center')\n    \n    # ëª¨ë“ˆ ì •ì˜\n    modules = [\n        {\"name\": \"main.py\", \"x\": 6, \"y\": 8.5, \"color\": \"#FFE0B2\", \"desc\": \"ë©”ì¸ ì‹¤í–‰\"},\n        {\"name\": \"audio/\", \"x\": 2, \"y\": 7, \"color\": \"#E1F5FE\", \"desc\": \"ìŒì„± ì²˜ë¦¬\"},\n        {\"name\": \"speech/\", \"x\": 6, \"y\": 7, \"color\": \"#E8F5E8\", \"desc\": \"ìŒì„±ì¸ì‹\"},\n        {\"name\": \"conversation/\", \"x\": 10, \"y\": 7, \"color\": \"#F3E5F5\", \"desc\": \"ëŒ€í™” ì²˜ë¦¬\"},\n        {\"name\": \"order/\", \"x\": 2, \"y\": 5, \"color\": \"#FFEBEE\", \"desc\": \"ì£¼ë¬¸ ê´€ë¦¬\"},\n        {\"name\": \"api/\", \"x\": 6, \"y\": 5, \"color\": \"#F1F8E9\", \"desc\": \"ì„œë²„ API\"},\n        {\"name\": \"utils/\", \"x\": 10, \"y\": 5, \"color\": \"#FFF3E0\", \"desc\": \"ìœ í‹¸ë¦¬í‹°\"},\n        {\"name\": \"models/\", \"x\": 4, \"y\": 3, \"color\": \"#E0F2F1\", \"desc\": \"ë°ì´í„° ëª¨ë¸\"},\n        {\"name\": \"config/\", \"x\": 8, \"y\": 3, \"color\": \"#FFF8E1\", \"desc\": \"ì„¤ì • ê´€ë¦¬\"}\n    ]\n    \n    # ëª¨ë“ˆ ê·¸ë¦¬ê¸°\n    for module in modules:\n        # ëª¨ë“ˆ ë°•ìŠ¤\n        rect = mpatches.Rectangle((module[\"x\"]-0.8, module[\"y\"]-0.4), 1.6, 0.8,\n                                 facecolor=module[\"color\"], edgecolor='black', linewidth=1)\n        ax.add_patch(rect)\n        \n        # ëª¨ë“ˆ ì´ë¦„\n        ax.text(module[\"x\"], module[\"y\"]+0.1, module[\"name\"], fontsize=11, fontweight='bold',\n                ha='center', va='center')\n        \n        # ëª¨ë“ˆ ì„¤ëª…\n        ax.text(module[\"x\"], module[\"y\"]-0.2, module[\"desc\"], fontsize=9,\n                ha='center', va='center', style='italic')\n    \n    # ì˜ì¡´ì„± í™”ì‚´í‘œ\n    dependencies = [\n        ((6, 8.1), (2, 7.4)),    # main -> audio\n        ((6, 8.1), (6, 7.4)),    # main -> speech\n        ((6, 8.1), (10, 7.4)),   # main -> conversation\n        ((6, 8.1), (6, 5.4)),    # main -> api\n        ((10, 6.6), (2, 5.4)),   # conversation -> order\n        ((6, 6.6), (4, 3.4)),    # speech -> models\n        ((10, 6.6), (8, 3.4)),   # conversation -> config\n    ]\n    \n    for start, end in dependencies:\n        ax.annotate('', xy=end, xytext=start,\n                   arrowprops=dict(arrowstyle='->', lw=1.5, color='gray', alpha=0.7))\n    \n    plt.tight_layout()\n    plt.savefig(output_dir / 'module_structure.png', dpi=300, bbox_inches='tight')\n    plt.close()\n    print(\"âœ… ëª¨ë“ˆ êµ¬ì¡° ì°¨íŠ¸ ìƒì„± ì™„ë£Œ\")\n\n# ëª¨ë“  ì°¨íŠ¸ ìƒì„±\nif __name__ == \"__main__\":\n    create_architecture_diagram()\n    create_performance_analysis()\n    create_technology_stack()\n    create_data_flow_diagram()\n    create_module_structure()\n    \n    print(f\"\\nğŸ‰ ëª¨ë“  ì°¨íŠ¸ê°€ '{output_dir}' ë””ë ‰í† ë¦¬ì— ìƒì„±ë˜ì—ˆìŠµë‹ˆë‹¤!\")\n    print(\"ìƒì„±ëœ íŒŒì¼ë“¤:\")\n    for chart_file in output_dir.glob('*.png'):\n        print(f\"  ğŸ“Š {chart_file.name}\")\n"